    const handleSave = () => {
        if (!desc || !amount){
            alert("Informe a descrição e o valor!");
            return;
        } else if(amount < 1){
            alert("O valor tem que ser positivo");
            return;
        }
    };

    return (
        <C.Container>
            <C.InputContent>
                <C.Label>Descrição</C.Label>
            </C.InputContent>
        </C.Container>
    );
};

